import React, { useEffect, useState } from "react";

export default function DisplaySpiceMerchant() {
  const [applications, setApplications] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editFormData, setEditFormData] = useState({});

  const role = localStorage.getItem("role");

  useEffect(() => {
    fetch("http://localhost:5001/orders/all")
      .then((res) => res.json())
      .then((data) => setApplications(data))
      .catch((err) => console.error(err));
  }, []);

  const handleEditClick = (application) => {
    setEditingId(application.id);
    setEditFormData(application);
  };

  const handleInputChange = (e) => {
    setEditFormData({ ...editFormData, [e.target.name]: e.target.value });
  };

  const handleUpdate = async () => {
    try {
      await fetch(`http://localhost:5001/orders/update/${editingId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(editFormData),
      });
      setApplications((prev) =>
        prev.map((app) => (app.id === editingId ? editFormData : app))
      );
      setEditingId(null);
    } catch (err) {
      console.error(err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:5001/orders/delete/${id}`, {
        method: "DELETE",
      });
      setApplications((prev) => prev.filter((app) => app.id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="merchant-container">
      <h2>Spice Merchants</h2>
      <table border="1" cellPadding="8">
        <thead>
          <tr>
            <th>Name</th>
            <th>Spices</th>
            <th>Experience</th>
            <th>Location</th>
            <th>Phone</th>
            <th>Email</th>
            {role === "ADMIN" || role === "FM" ? <th>Actions</th> : null}
          </tr>
        </thead>
        <tbody>
          {applications.map((app) => (
            <tr key={app.id}>
              {editingId === app.id ? (
                <>
                  <td>
                    <input
                      name="name"
                      value={editFormData.name}
                      onChange={handleInputChange}
                    />
                  </td>
                  <td>
                    <input
                      name="spices"
                      value={editFormData.spices}
                      onChange={handleInputChange}
                    />
                  </td>
                  <td>
                    <input
                      name="experience"
                      value={editFormData.experience}
                      onChange={handleInputChange}
                    />
                  </td>
                  <td>
                    <input
                      name="location"
                      value={editFormData.location}
                      onChange={handleInputChange}
                    />
                  </td>
                  <td>
                    <input
                      name="phoneNumber"
                      value={editFormData.phoneNumber}
                      onChange={handleInputChange}
                    />
                  </td>
                  <td>
                    <input
                      name="email"
                      value={editFormData.email}
                      onChange={handleInputChange}
                    />
                  </td>
                  <td>
                    <button onClick={handleUpdate}>Update</button>
                    <button onClick={() => setEditingId(null)}>Cancel</button>
                  </td>
                </>
              ) : (
                <>
                  <td>{app.name}</td>
                  <td>{app.spices}</td>
                  <td>{app.experience}</td>
                  <td>{app.location}</td>
                  <td>{app.phoneNumber}</td>
                  <td>{app.email}</td>
                  {role === "ADMIN" || role === "FM" ? (
                    <td>
                      <button onClick={() => handleEditClick(app)}>Edit</button>
                      <button onClick={() => handleDelete(app.id)}>Delete</button>
                    </td>
                  ) : null}
                </>
              )}
            </tr>
          ))}
        </tbody>
      </table>
