import React, { useEffect, useState } from "react";

function DisplaySpiceMerchant() {
  const [merchants, setMerchants] = useState([]);
  const [search, setSearch] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({
    name: "",
    spices: "",
    experience: "",
    storeLocation: "",
    phoneNumber: "",
    status: "",
  });

  useEffect(() => {
    fetchMerchants();
  }, []);

  const fetchMerchants = () => {
    fetch("http://localhost:5001/getAllSpiceMerchants")
      .then((res) => res.json())
      .then((data) => setMerchants(data))
      .catch((err) => console.error(err));
  };

  const handleEditClick = (merchant) => {
    setEditingId(merchant.id);
    setEditForm(merchant);
  };

  const handleCancel = () => {
    setEditingId(null);
    setEditForm({});
  };

  const handleChange = (e) => {
    setEditForm({ ...editForm, [e.target.name]: e.target.value });
  };

  const handleUpdate = () => {
    fetch(`http://localhost:5001/updateSpiceMerchant/${editingId}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(editForm),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Update failed");
        return res.json();
      })
      .then(() => {
        fetchMerchants(); // refresh table
        setEditingId(null);
      })
      .catch((err) => console.error(err));
  };

  // Filter merchants based on search input
  const filteredMerchants = merchants.filter((m) =>
    m.name.toLowerCase().includes(search.toLowerCase()) ||
    m.spices.toLowerCase().includes(search.toLowerCase()) ||
    m.storeLocation.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <h2>Spice Merchant Applications</h2>

      {/* Search input */}
      <input
        type="text"
        placeholder="Search by name, spice, or location"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{ marginBottom: "10px", padding: "5px", width: "300px" }}
      />

      {filteredMerchants.length > 0 ? (
        <table border="1" cellPadding="8">
          <thead>
            <tr>
              <th>Name</th>
              <th>Spices</th>
              <th>Experience</th>
              <th>Store Location</th>
              <th>Phone</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredMerchants.map((m) => (
              <tr key={m.id}>
                {editingId === m.id ? (
                  <>
                    <td><input name="name" value={editForm.name} onChange={handleChange} /></td>
                    <td><input name="spices" value={editForm.spices} onChange={handleChange} /></td>
                    <td><input name="experience" value={editForm.experience} onChange={handleChange} /></td>
                    <td><input name="storeLocation" value={editForm.storeLocation} onChange={handleChange} /></td>
                    <td><input name="phoneNumber" value={editForm.phoneNumber} onChange={handleChange} /></td>
                    <td><input name="status" value={editForm.status} onChange={handleChange} /></td>
                    <td>
                      <button onClick={handleUpdate}>Save</button>
                      <button onClick={handleCancel}>Cancel</button>
                    </td>
                  </>
                ) : (
                  <>
                    <td>{m.name}</td>
                    <td>{m.spices}</td>
                    <td>{m.experience}</td>
                    <td>{m.storeLocation}</td>
                    <td>{m.phoneNumber}</td>
                    <td>{m.status}</td>
                    <td>
                      <button onClick={() => handleEditClick(m)}>Edit</button>
                    </td>
                  </>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No merchants found</p>
      )}
    </div>
  );
}

export default DisplaySpiceMerchant;
