import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";
import Home from "./components/Home";
import NavBar from "./components/NavBar";
import ApplyForm from "./components/ApplyForm";
import DisplaySpiceMerchant from "./components/DisplaySpiceMerchant";
import Footer from "./components/Footer";
import About from "./components/About";
import ContactUs from "./components/ContactUs";
import OurProducts from "./components/OurProducts";
import Login from "./components/Login";
import Register from "./components/Register";
import PrivateRoute from "./components/PrivateRoute";
import Welcome from "./components/WelcomePage";

function AppContent() {
  const location = useLocation();
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Only set logged in if token exists AND we are not on the welcome page initially
    const token = localStorage.getItem("token");
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  return (
    <div className="app" style={{ position: "relative" }}>
      {/* Show NavBar only if logged in and not on Welcome page */}
      {isLoggedIn && location.pathname !== "/"
      &&location.pathname !== "/login"&&location.pathname !== "/register" && <NavBar />}

      <Routes>
        {/* Always start here on fresh load */}
        <Route path="/" element={<Welcome />} />

        {/* Public routes */}
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* Protected routes */}
        <Route
          path="/home"
          element={
            <PrivateRoute>
              <Home />
            </PrivateRoute>
          }
        />
        <Route
          path="/apply"
          element={
            <PrivateRoute>
              <ApplyForm />
            </PrivateRoute>
          }
        />
        <Route
          path="/getAllSpiceMerchants"
          element={
            <PrivateRoute>
              <DisplaySpiceMerchant />
            </PrivateRoute>
          }
        />
        <Route
          path="/about"
          element={
            <PrivateRoute>
              <About />
            </PrivateRoute>
          }
        />
        <Route
          path="/ourproducts"
          element={
            <PrivateRoute>
              <OurProducts />
            </PrivateRoute>
          }
        />
        <Route
          path="/contactus"
          element={
            <PrivateRoute>
              <ContactUs />
            </PrivateRoute>
          }
        />
      </Routes>

      {/* Show Footer only if logged in and not on Welcome page */}
      {isLoggedIn && location.pathname !== "/" && <Footer />}
    </div>
  );
}

export default function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}
