import React, { useEffect, useState } from "react";

export default function DisplaySpiceMerchant() {
  const [merchants, setMerchants] = useState([]);
  const [search, setSearch] = useState("");
  const role = localStorage.getItem("role");
  const email = localStorage.getItem("email");

  useEffect(() => {
    const fetchData = async () => {
      try {
        let empLocation = null;

        if (role === "EVALUATOR" || role === "TERRITORY_MANAGER") {
          const empRes = await fetch(`http://localhost:5001/employees/byEmail/${email}`);
          const employee = await empRes.json();
          empLocation = employee.location;
        }

        const res = await fetch("http://localhost:5001/getAllSpiceMerchants");
        let data = await res.json();

        if (empLocation) {
          data = data.filter((m) => m.location === empLocation);
        }

        setMerchants(data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchData();
  }, [role, email]);

  const filteredMerchants = merchants.filter(
    (m) =>
      m.name.toLowerCase().includes(search.toLowerCase()) ||
      m.spices.toLowerCase().includes(search.toLowerCase()) ||
      m.location.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <h2>Spice Merchants</h2>
      <input
        type="text"
        placeholder="Search merchants..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      <table border="1">
        <thead>
          <tr>
            <th>Name</th>
            <th>Spices</th>
            <th>Experience</th>
            <th>Location</th>
            <th>Email</th>
            <th>Phone</th>
            {role === "ADMIN" && <th>Actions</th>}
            {role === "FRANCHISE_MANAGER" && <th>Delete</th>}
          </tr>
        </thead>
        <tbody>
          {filteredMerchants.map((m) => (
            <tr key={m.id}>
              <td>{m.name}</td>
              <td>{m.spices}</td>
              <td>{m.experience}</td>
              <td>{m.location}</td>
              <td>{m.email}</td>
              <td>{m.phoneNumber}</td>
              {role === "ADMIN" && <td><button>Edit</button></td>}
              {role === "FRANCHISE_MANAGER" && <td><button>Delete</button></td>}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
