import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";

export default function Welcome() {
  const navigate = useNavigate();
  const [showMore, setShowMore] = useState(false);
  const moreRef = useRef(null);

  const handleExplore = () => {
    setShowMore(true);
    // Scroll to newly revealed section
    setTimeout(() => {
      if (moreRef.current) {
        moreRef.current.scrollIntoView({ behavior: "smooth" });
      }
    }, 100);
  };

  return (
    <div className="d-flex flex-column justify-content-center align-items-center bg-light p-4">
      {/* Top navigation buttons */}
      <div className="mb-4 d-flex gap-2">
        <button
          onClick={() => navigate("/login")}
          className="btn btn-warning btn-sm shadow"
        >
          Login
        </button>
        <button
          onClick={() => navigate("/register")}
          className="btn btn-success btn-sm shadow"
        >
          Register
        </button>
      </div>

      {/* Main welcome section */}
      <h1 className="display-5 fw-bold text-center text-dark mb-3">
        Welcome to Spice Merchant Portal
      </h1>
      <p
        className="lead text-center text-dark mb-4"
        style={{ maxWidth: "600px" }}
      >
        Discover a world of rich flavors and aromatic spices. Our portal helps
        spice merchants connect, trade, and share their finest blends. Whether
        you’re here to buy, sell, or explore, you’re in the right place!
      </p>

      {/* Explore More Button */}
      {!showMore && (
        <div className="text-center">
          <button
            onClick={handleExplore}
            className="btn btn-outline-primary"
          >
            Explore More
          </button>
        </div>
      )}

      {/* Expanded Section */}
      {showMore && (
        <div ref={moreRef} className="mt-5 w-100">
          {/* About Section */}
          <div className="bg-white p-4 rounded shadow mb-4">
            <h2>About Our Portal</h2>
            <p>
              We are a passionate team dedicated to connecting spice merchants
              and enthusiasts. Our platform helps you discover, trade, and
              explore the finest spices with ease and confidence.
            </p>
          </div>

          {/* Contact Section */}
          <div className="bg-light p-4 rounded shadow">
            <h2>Contact Us</h2>
            <p>Email: info@spicemerchant.com</p>
            <p>Phone: +91 98765 43210</p>
            <p>Address: 123 Spice Street, Salem, India</p>
          </div>
        </div>
      )}
    </div>
  );
}
