import React, { useState, useEffect } from "react";
import "./EmployeeTable.css";

export default function EmployeeTable() {
  const [employees, setEmployees] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    role: "",
  });
  const [search, setSearch] = useState("");
  const [editId, setEditId] = useState(null);

  // Fetch all employees
  const fetchEmployees = async () => {
    try {
      const res = await fetch("http://localhost:5001/employees");
      const data = await res.json();
      setEmployees(data);
    } catch (err) {
      console.error("Error fetching employees:", err);
    }
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (editId) {
        await fetch(`http://localhost:5001/employees/${editId}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(formData),
        });
        setEditId(null);
      } else {
        await fetch("http://localhost:5001/employees", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(formData),
        });
      }

      setFormData({ name: "", email: "", phone: "", role: "" });
      fetchEmployees();
    } catch (err) {
      console.error("Error saving employee:", err);
    }
  };

  const handleDelete = async (id) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this employee?");
    if (!confirmDelete) {
      return; // If user cancels, do nothing
    }

    try {
      await fetch(`http://localhost:5001/employees/${id}`, {
        method: "DELETE",
      });
      fetchEmployees();
    } catch (err) {
      console.error("Error deleting employee:", err);
    }
  };


  const handleEdit = (emp) => {
    setFormData({
      name: emp.name,
      email: emp.email,
      phone: emp.phone,
      role: emp.role,
    });
    setEditId(emp.id);
  };

  const filteredEmployees = employees.filter(
    (emp) =>
      emp.name.toLowerCase().includes(search.toLowerCase()) ||
      emp.email.toLowerCase().includes(search.toLowerCase()) ||
      emp.role.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="employee-container">
      <h2>Employee Management</h2>

      {/* Search */}
      <input
        type="text"
        placeholder="Search by name, email or role..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="search-input"
      />

      {/* Add / Edit Form */}
      <form onSubmit={handleSubmit} className="add-form">
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="phone"
          placeholder="Phone"
          value={formData.phone}
          onChange={handleChange}
          required
        />
        <select
          name="role"
          value={formData.role}
          onChange={handleChange}
          required
        >
          <option value="">Select Role</option>
          <option value="EVALUATOR">Evaluator</option>
          <option value="TERRITORY_MANAGER">Territory Manager</option>
          <option value="FRANCHISE_MANAGER">Franchise Manager</option>
        </select>
        <button type="submit">
          {editId ? "Update Employee" : "Add Employee"}
        </button>
      </form>

      <div className="table-wrapper">
  <table>
    <thead>
      <tr className="sticky">
        <th  style={{ width: "60px" }}>ID</th>
        <th>Name</th>
        <th>Email</th>
        <th>Phone</th>
        <th>Role</th>
        <th style={{ width: "160px" }}>Actions</th>
      </tr>
    </thead>
    <tbody>
      {filteredEmployees.length > 0 ? (
        filteredEmployees.map((emp) => (
          <tr key={emp.id}>
            <td  data-label="ID">{emp.id}</td>
            <td data-label="Name">{emp.name}</td>
            <td data-label="Email">{emp.email}</td>
            <td data-label="Phone">{emp.phone}</td>
            <td data-label="Role">{emp.role}</td>
            <td data-label="Actions">
  <div className="action-buttons">
    <button className="edit-btn" onClick={() => handleEdit(emp)}>Edit</button>
    <button className="delete-btn" onClick={() => handleDelete(emp.id)}>Delete</button>
  </div>
</td>
          </tr>
        ))
      ) : (
        <tr>
          <td className="sticky-col" colSpan="6">No employees found</td>
        </tr>
      )}
    </tbody>
  </table>
</div>


    </div>
  );
}
