import React from "react";
import { useNavigate } from "react-router-dom";

export default function Welcome() {
  const navigate = useNavigate();

  return (
    <div className="flex flex-col items-center min-h-screen bg-gradient-to-b from-yellow-100 via-orange-50 to-yellow-200 p-8 font-sans">
      {/* Top navigation buttons */}
      <div className="mb-8 flex space-x-4">
        <button
          onClick={() => navigate("/login")}
          className="px-5 py-2 text-sm bg-orange-500 text-white rounded-lg shadow-md hover:bg-orange-600 transition duration-300"
        >
          Login
        </button>
        <button
          onClick={() => navigate("/register")}
          className="px-5 py-2 text-sm bg-green-500 text-white rounded-lg shadow-md hover:bg-green-600 transition duration-300"
        >
          Register
        </button>
      </div>

      {/* Main welcome section */}
      <h1 className="text-3xl font-bold text-yellow-900 mb-4 text-center">
        Welcome to Spice Merchant Portal
      </h1>
      <p className="text-lg text-gray-700 max-w-xl text-center leading-relaxed">
        Discover a world of rich flavors and aromatic spices. Our portal helps
        spice merchants connect, trade, and share their finest blends. Whether
        you’re here to buy, sell, or explore, you’re in the right place!
      </p>


    </div>
  );
}
