import React from "react";
import { useNavigate } from "react-router-dom";

export default function Welcome() {
  const navigate = useNavigate();

  return (
    <div className="d-flex flex-column justify-content-center align-items-center ">

      {/* Top navigation buttons */}
      <div className="mb-4 d-flex gap-2">
        <button
          onClick={() => navigate("/login")}
          className="btn btn-warning btn-sm shadow"
        >
          Login
        </button>
        <button
          onClick={() => navigate("/register")}
          className="btn btn-success btn-sm shadow"
        >
          Register
        </button>
      </div>

      {/* Main welcome section */}
      <h1 className="display-5 fw-bold text-center text-dark mb-3">
        Welcome to Spice Merchant Portal
      </h1>
      <p className="lead text-center text-balc mb-4" style={{ maxWidth: "600px" }}>
        Discover a world of rich flavors and aromatic spices. Our portal helps
        spice merchants connect, trade, and share their finest blends. Whether
        you’re here to buy, sell, or explore, you’re in the right place!
      </p>

      {/* Optional interactive feature: scroll down hint */}
      <div className="text-center">
        <button
          onClick={() => window.scrollTo({ top: 600, behavior: "smooth" })}
          className="btn btn-outline-primary"
        >
          Explore More
        </button>
      </div>
    </div>
  );
}
