import React, { useEffect, useState } from "react";
import "./GuestStatus.css";

export default function GuestStatus() {
  const [status, setStatus] = useState("");
  const [loading, setLoading] = useState(true);
  const [errorMsg, setErrorMsg] = useState("");
  const email = localStorage.getItem("guestEmail");

  useEffect(() => {
    if (email) {
      fetch(`http://localhost:5001/guest/status/${email}`)
        .then((res) => {
          if (!res.ok) throw new Error("Failed to fetch status");
          return res.text();
        })
        .then((data) => {
          setStatus(data);
          setLoading(false);
          setErrorMsg("");
        })
        .catch(() => {
          setStatus("");
          setLoading(false);
          setErrorMsg("Error fetching status. Please try again later.");
        });
    } else {
      setLoading(false);
      setErrorMsg("No guest email found. Please login again.");
    }
  }, [email]);

  return (
    <div className="guest-status-container">
      <h2 className="guest-status-header">Application Status</h2>
      {!email && <p className="error-message">No guest email found. Please login again.</p>}
      {loading && <p className="loading-message">Loading your application status...</p>}
      {!loading && email && !errorMsg && (
        <div className="status-card">
          <p>
            <strong>Email:</strong> <span className="email-value">{email}</span>
          </p>
          <p>
            <strong>Status:</strong>{" "}
            <span className={`status-value ${status.toLowerCase()}`}>
              {status || "No status available"}
            </span>
          </p>
        </div>
      )}
      {!loading && errorMsg && <p className="error-message">{errorMsg}</p>}
    </div>
  );
}
