import React from "react";
import { useNavigate } from "react-router-dom";

export default function Welcome() {
  const navigate = useNavigate();

  return (
    <div
      style={{
        textAlign: "center",
        padding: "3rem",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      {/* Top navigation buttons */}
      <div style={{ marginBottom: "2rem" }}>
        <button
          onClick={() => navigate("/login")}
          style={{
            padding: "10px 20px",
            margin: "0 10px",
            fontSize: "14px",
            background: "#ff9800",
            border: "none",
            color: "white",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Login
        </button>
        <button
          onClick={() => navigate("/register")}
          style={{
            padding: "10px 20px",
            margin: "0 10px",
            fontSize: "14px",
            background: "#4caf50",
            border: "none",
            color: "white",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Register
        </button>
      </div>

      {/* Main welcome section */}
      <h1 style={{ color: "#4b2e05", marginBottom: "1rem" }}>
        Welcome to Spice Merchant Portal
      </h1>
      <p style={{ fontSize: "18px", maxWidth: "600px", margin: "auto" }}>
        Discover a world of rich flavors and aromatic spices. Our portal helps
        spice merchants connect, trade, and share their finest blends. Whether
        you’re here to buy, sell, or explore, you’re in the right place!
      </p>

      {/* Call-to-action buttons */}

    </div>
  );
}
