import React, { useEffect, useState } from "react";
import "./AdminOrders.css";

export default function AdminOrders() {
  const [orders, setOrders] = useState([]);

  // Fetch all orders
  const fetchOrders = () => {
    fetch("http://localhost:5001/orders/all")
      .then((res) => res.json())
      .then((data) => setOrders(data))
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  // Update status to FINISHED
  const updateStatus = (id) => {
    fetch(`http://localhost:5001/orders/update-status/${id}?status=FINISHED`, {
      method: "PUT",
    })
      .then(() => fetchOrders())
      .catch((err) => console.error(err));
  };

  return (
    <div className="admin-orders-container">
      <h2>All Orders</h2>
      {orders.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Vendor Email</th>
              <th>Spices</th>
              <th>Quantity</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order.id}>
                <td data-label="Vendor Email">{order.vendorEmail}</td>
                {/* Join spiceNames list into a comma-separated string */}
                <td data-label="Spices">{order.spiceNames.join(", ")}</td>
                <td data-label="Quantity">{order.quantity}</td>
                <td data-label="Status">
                  {order.status === "FINISHED" ? (
                    <span className="status-finished">✔ Finished</span>
                  ) : (
                    <button onClick={() => updateStatus(order.id)}>
                      Mark as Finished
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No orders available.</p>
      )}
    </div>
  );
}
